drop table if exists invoice;
create table invoice (invoiceNumber integer not null, caryOnOrCargoBooking varchar(50), settlement varchar(50), size integer, charge integer, flightCode varchar(50), customerCode varchar(50), dealer varchar(50), personInCharge varchar(50), receiptDate date, deliveryDate date, receiptVoucher varchar(50), invoiceType varchar(50), address varchar(50), goods varchar(50), client varchar(50), requestDate date, fullName varchar(50), postalCode varchar(50), telephoneNumber varchar(50), destination varchar(50), position varchar(50), goodsCode varchar(50), goodsName varchar(50), pickup varchar(50), GolfOrSkiOrAirport varchar(50), clubNumber varchar(50), service varchar(50), primary key (invoiceNumber));
